	const char* CommTickResultCore::getCompiledHash()
	{
		return CommYARP_BTIDL::REPO_HASH;
	}
	
	void CommTickResultCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommTickResultCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommTickResultCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommTickResultCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.result);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommTickResultCore::CommTickResultCore()
	:	idl_CommTickResult()
	{  
		setResult(CommYARP_BT::TickResult());
	}
	
	CommTickResultCore::CommTickResultCore(const DATATYPE &data)
	:	idl_CommTickResult(data)
	{  }

	CommTickResultCore::~CommTickResultCore()
	{  }
	
	void CommTickResultCore::to_ostream(std::ostream &os) const
	{
	  os << "CommTickResult(";
	  getResult().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommTickResultCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<result>";
		getResult().to_xml(os, indent);
		os << indent << "</result>";
	}
	
	// restore from xml stream
	void CommTickResultCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_result("<result>");
		
		if(kmp_result.search(is)) {
			CommYARP_BT::TickResult resultItem;
			resultItem.from_xml(is);
			setResult(resultItem);
		}
	}
	
